package account.model.entity;import jakarta.persistence.*;import jakarta.validation.constraints.Size;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import lombok.ToString;import java.util.HashSet;import java.util.Set;@Entity@Getter@Setter@ToString@NoArgsConstructorpublic class AppUser {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(nullable = false)    private Long id;    private String name;    private String lastname;    @Column(unique = true)    private String email;    @Size(min = 12)    private String password;    @ManyToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "group_id")    private Group group;    @ManyToMany(fetch = FetchType.EAGER)    @JoinTable(            name = "user_roles",            joinColumns = @JoinColumn(name = "user_id"),            inverseJoinColumns = @JoinColumn(name = "role_id")    )    private Set<Role> roles = new HashSet<>();    private boolean isAccountNonLocked;    private int failedAttempt;}