package account.model.validator;import jakarta.validation.ConstraintValidator;import jakarta.validation.ConstraintValidatorContext;public class EmailDomainValidator implements        ConstraintValidator<ValidEmailDomain, String> {    private String domain;    @Override    public void initialize(ValidEmailDomain constraintAnnotation) {        this.domain = constraintAnnotation.domain();    }    @Override    public boolean isValid(String value, ConstraintValidatorContext context) {        if (value == null) {            return false;        }        return value.endsWith("@" + domain);    }}